    <script>
        // 初始化Mermaid图表
        mermaid.initialize({
            startOnLoad: true,
            theme: 'base',
            themeVariables: {
                primaryColor: '#FEEBC8',
                nodeBorder: '#9C4221',
                lineColor: '#D97706',
                textColor: '#5C4033',
                primaryBorderColor: '#9C4221'
            },
            flowchart: {
                useMaxWidth: true,
                htmlLabels: true,
                curve: 'basis'
            }
        });

        // 为关键词标签添加点击效果
        document.querySelectorAll('.keyword-tag').forEach(tag => {
            tag.addEventListener('click', function() {
                this.classList.toggle('bg-amber-300');
                this.classList.toggle('scale-105');
            });
        });

        // 为卡片添加悬停效果
        document.querySelectorAll('.topic-card, .quote-card').forEach(card => {
            card.addEventListener('mouseenter', function() {
                this.style.transform = 'translateY(-4px)';
                this.style.boxShadow = '0 10px 25px -5px rgba(0,0,0,0.1)';
            });
            card.addEventListener('mouseleave', function() {
                this.style.transform = '';
                this.style.boxShadow = '';
            });
        });
    </script>
