    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
    <style>
        :root {
            --primary: #FF7F50;
            --secondary: #FFA07A;
            --accent: #FF6347;
            --light: #FFE4B5;
            --dark: #8B4513;
            --text: #5C4033;
            --bg: #FFF8DC;
        }

        body {
            font-family: 'PingFang SC', 'Microsoft YaHei', sans-serif;
            background-color: var(--bg);
            color: var(--text);
            line-height: 1.6;
            margin: 0;
            padding: 0;
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 20px;
        }

        header {
            text-align: center;
            padding: 30px 0;
            background: linear-gradient(135deg, var(--primary), var(--accent));
            color: white;
            border-radius: 15px;
            margin-bottom: 30px;
            box-shadow: 0 10px 20px rgba(0,0,0,0.1);
        }

        h1 {
            font-size: 2.5rem;
            margin: 0;
        }

        h2 {
            color: var(--dark);
            border-bottom: 2px solid var(--secondary);
            padding-bottom: 10px;
            margin-top: 40px;
        }

        h3 {
            color: var(--accent);
            margin-top: 25px;
        }

        .card {
            background-color: white;
            border-radius: 12px;
            padding: 20px;
            margin-bottom: 30px;
            box-shadow: 0 5px 15px rgba(0,0,0,0.05);
            transition: transform 0.3s, box-shadow 0.3s;
        }

        .card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 25px rgba(0,0,0,0.1);
        }

        .keyword-tag {
            display: inline-block;
            background-color: var(--light);
            color: var(--dark);
            padding: 5px 15px;
            border-radius: 20px;
            margin: 5px;
            font-weight: 600;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }

        .message {
            margin: 15px 0;
            padding: 15px;
            border-radius: 10px;
            background-color: #FFF5EE;
            position: relative;
            max-width: 80%;
        }

        .message.sender {
            margin-left: auto;
            background-color: var(--light);
        }

        .message-info {
            font-size: 0.8rem;
            color: var(--dark);
            margin-bottom: 5px;
            display: flex;
            justify-content: space-between;
        }

        .quote {
            font-style: italic;
            padding: 15px;
            background-color: #FFF5EE;
            border-left: 4px solid var(--accent);
            margin: 20px 0;
        }

        .quote-author {
            text-align: right;
            font-weight: bold;
            color: var(--dark);
        }

        .resource {
            margin: 10px 0;
            padding: 10px;
            background-color: #FFF5EE;
            border-radius: 8px;
        }

        .resource a {
            color: var(--accent);
            text-decoration: none;
            font-weight: 600;
        }

        .resource a:hover {
            text-decoration: underline;
        }

        .chart-container {
            position: relative;
            height: 300px;
            margin: 30px 0;
        }

        .mermaid {
            background-color: white;
            padding: 20px;
            border-radius: 10px;
            margin: 20px 0;
        }

        .stats-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: 20px;
            margin: 30px 0;
        }

        .stat-card {
            background-color: white;
            border-radius: 10px;
            padding: 20px;
            text-align: center;
            box-shadow: 0 5px 15px rgba(0,0,0,0.05);
        }

        .stat-number {
            font-size: 2.5rem;
            color: var(--accent);
            font-weight: bold;
            margin: 10px 0;
        }

        .stat-label {
            color: var(--dark);
            font-size: 0.9rem;
        }

        @media (max-width: 768px) {
            h1 {
                font-size: 1.8rem;
            }

            .stats-grid {
                grid-template-columns: 1fr;
            }

            .message {
                max-width: 100%;
            }
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
            <h1>AI 编程互助会 07 群</h1>
            <p>2025年6月17日 聊天精华报告</p>
        </header>

        <div class="stats-grid">
            <div class="stat-card">
                <div class="stat-number">500</div>
                <div class="stat-label">总消息数</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">434</div>
                <div class="stat-label">有效文本消息</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">50</div>
                <div class="stat-label">活跃用户数</div>
            </div>
            <div class="stat-card">
                <div class="stat-number">14h</div>
                <div class="stat-label">讨论时长</div>
            </div>
        </div>

        <h2>核心关键词</h2>
        <div class="card">
            <span class="keyword-tag">AI编程</span>
            <span class="keyword-tag">Cursor</span>
            <span class="keyword-tag">自媒体运营</span>
            <span class="keyword-tag">产品变现</span>
            <span class="keyword-tag">声音克隆</span>
            <span class="keyword-tag">项目管理</span>
            <span class="keyword-tag">独立开发</span>
            <span class="keyword-tag">社群增长</span>
        </div>

        <h2>核心概念关系图</h2>
        <div class="mermaid">
            flowchart LR
                A[AI编程] --> B(Cursor工具)
                A --> C(独立开发)
                C --> D(产品变现)
                D --> E(自媒体运营)
                E --> F(社群增长)
                A --> G(声音克隆)
                G --> H(Minimax)
                C --> I(项目管理)
                I --> J(飞书/TAPD)
        </div>

        <h2>活跃用户排行榜</h2>
        <div class="chart-container">
            <canvas id="userChart"></canvas>
        </div>

        <h2>精华话题聚焦</h2>

        <div class="card">
            <h3>1. AI编程工具Cursor的使用与变现</h3>
            <p>群内深入讨论了Cursor这一AI编程工具的使用体验、商业版本差异以及如何通过AI编程实现变现。多位用户分享了各自的使用技巧和变现路径。</p>

            <h4>重要对话节选</h4>
            <div class="message">
                <div class="message-info">
                    <span class="sender">超级峰</span>
                    <span class="time">08:50:33</span>
                </div>
                <div class="message-content">腾讯新出的一个混元3D，2D直转3D，感觉app之类可以用？</div>
            </div>

            <div class="message">
                <div class="message-info">
                    <span class="sender">程序杂念</span>
                    <span class="time">13:49:26</span>
                </div>
                <div class="message-content">我用 Cursor + Task Master 可以用力榨干 Cursor，我只需要写出我的大需求，AI 会拆分需求，然后 Cursror 的 AI 就会自动安排 自动执行。真的好爽，TaskMaster 安排任务，Cursor 负责干活。</div>
            </div>

            <div class="message sender">
                <div class="message-info">
                    <span class="sender">超级峰</span>
                    <span class="time">16:17:56</span>
                </div>
                <div class="message-content">近一个月，应该用了2000 多次左右吧，差不多 30w 行</div>
            </div>
        </div>

        <div class="card">
            <h3>2. 自媒体运营与个人品牌建设</h3>
            <p>群内对自媒体运营策略、个人品牌建设和粉丝筛选机制进行了热烈讨论，特别分析了"花生"这一案例的成功路径和争议点。</p>

            <h4>重要对话节选</h4>
            <div class="message">
                <div class="message-info">
                    <span class="sender">好记星</span>
                    <span class="time">09:57:01</span>
                </div>
                <div class="message-content">我一直在观察花生的言论，如果说无默默无闻到自吹自擂有一个光谱的话，他的言论显然是在光谱的自吹自擂这一端，说实话我是有点反感的，反感的点在于我认为他混淆了运气和实力，但他不遗余力的持续宣传自己这个很强，那个也很强，甚至运气都很好， why? baby why? 作为一个多年的老运营，他的情商应该完全能理解这样说话会引起一部分人的反感，为啥还持续保持这样（你看他之前视频就不是这样）呢。后来我想明白了，这是一个很好的筛选粉丝的机制，能接受这个言论的粉才是真的粉，在他们心里不是反感而是崇拜，这种粉丝更有黏性。</div>
            </div>

            <div class="message sender">
                <div class="message-info">
                    <span class="sender">超级峰</span>
                    <span class="time">10:52:41</span>
                </div>
                <div class="message-content">还是有点认可这个说法的，花生目前的名气确实是自己炒热起来的，黑红也是红，从一开始补光灯起势可能突然来的名气确实也让他有点浮躁了，印象中还是比较喜欢出名之前的务实做视频、内容时候的他，hahha 观望一下</div>
            </div>
        </div>

        <div class="card">
            <h3>3. 声音克隆技术与应用</h3>
            <p>群内探讨了声音克隆技术的最新进展，特别是Minimax等平台的服务，以及如何将这项技术应用于实际产品开发中。</p>

            <h4>重要对话节选</h4>
            <div class="message">
                <div class="message-info">
                    <span class="sender">光源</span>
                    <span class="time">18:40:09</span>
                </div>
                <div class="message-content">我现在就是用 minimax 克隆 + mcp，直接让 cursor 生成 TTS</div>
            </div>

            <div class="message sender">
                <div class="message-info">
                    <span class="sender">光源</span>
                    <span class="time">18:47:12</span>
                </div>
                <div class="message-content">把自己声音克隆一下，再通过 minimax mcp 生成 TTS，省得自己录了。普通话也更标准。[奸笑]</div>
            </div>
        </div>

        <h2>群友金句闪耀</h2>
        <div class="card">
            <div class="quote">
                "自媒体要么接广告 要么卖自己的产品 社群服务。但是有一部分喜欢包装成自己通过咨询或者企业授课，这种赚钱的可能看起来高大上一些吧"
                <div class="quote-author">— Panda, 10:05:04</div>
            </div>

            <div class="quote">
                "花生的收入是很多元的。如果想做自媒体，建议仔细研究下花生"
                <div class="quote-author">— 超级峰, 10:05:51</div>
            </div>

            <div class="quote">
                "高认知群体现在要收割情绪和个人成长的"
                <div class="quote-author">— Silver, 10:06:59</div>
            </div>

            <div class="quote">
                "网络上混 身份是自己给的"
                <div class="quote-author">— 七月, 10:01:36</div>
            </div>
        </div>

        <h2>提及产品与资源</h2>
        <div class="card">
            <div class="resource">
                <strong>Cursor</strong>: AI编程辅助工具，支持代码生成和自动补全
            </div>

            <div class="resource">
                <strong>Minimax</strong>: 提供声音克隆和文本转语音服务的AI平台
            </div>

            <div class="resource">
                <a href="https://x.com/rowancheung/status/1934518092891086855?s=46&t=Usm-q_K1CjxyOi0lKv7NaA" target="_blank">腾讯混元3D技术介绍</a>
            </div>

            <div class="resource">
                <a href="https://agent.minimax.io/" target="_blank">Minimax agent平台</a>
            </div>
        </div>

        <h2>消息时间分布</h2>
        <div class="chart-container">
            <canvas id="timeChart"></canvas>
        </div>
    </div>

    <script>
        // 活跃用户数据
        const userData = {
            labels: ['超级峰', '离黍', '好记星', 'Dulk', 'Silver', '其他'],
            datasets: [{
                label: '发言数量',
                data: [108, 39, 38, 30, 21, 264],
                backgroundColor: [
                    '#FF7F50',
                    '#FFA07A',
                    '#FF6347',
                    '#FF8C69',
                    '#FF4500',
                    '#FFDAB9'
                ],
                borderColor: [
                    '#E9967A',
                    '#CD5C5C',
                    '#FF6347',
                    '#FF8C69',
                    '#FF4500',
                    '#FFDAB9'
                ],
                borderWidth: 1
            }]
        };

        // 消息时间分布数据
        const timeData = {
            labels: ['6-8', '8-10', '10-12', '12-14', '14-16', '16-18', '18-20'],
            datasets: [{
                label: '消息数量',
                data: [15, 120, 85, 60, 75, 90, 55],
                backgroundColor: 'rgba(255, 159, 64, 0.2)',
                borderColor: 'rgba(255, 159, 64, 1)',
                borderWidth: 2,
                tension: 0.4,
                fill: true
            }]
        };

        // 初始化用户图表
        const userCtx = document.getElementById('userChart').getContext('2d');
        new Chart(userCtx, {
            type: 'bar',
            data: userData,
            options: {
                responsive: true,
                plugins: {
                    legend: {
                        position: 'top',
                    },
                    title: {
                        display: true,
                        text: '活跃用户发言数量统计'
                    }
                },
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });

        // 初始化时间分布图表
        const timeCtx = document.getElementById('timeChart').getContext('2d');
        new Chart(timeCtx, {
            type: 'line',
            data: timeData,
            options: {
                responsive: true,
                plugins: {
                    legend: {
                        position: 'top',
                    },
                    title: {
                        display: true,
                        text: '消息时间分布'
                    }
                },
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });

        // 初始化Mermaid
        mermaid.initialize({
            startOnLoad: true,
            theme: 'default',
            themeVariables: {
                primaryColor: '#FFE4B5',
                nodeBorder: '#8B4513',
                lineColor: '#A0522D',
                textColor: '#5C4033'
            }
        });
    </script>
</body>
