            // --- Render Hourly Activity Chart ---
            const hourlyCtx = document.getElementById('hourlyActivityChart').getContext('2d');
            new Chart(hourlyCtx, {
                type: 'line',
                data: hourlyActivityData,
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                         y: {
                            beginAtZero: true,
                             grid: { 
                                color: '#FDE68A',
                                Codedash: [2, 4]
                            }
                        },
                        x: {
                            grid: { display: false }
                        }
                    },
                    plugins: {
                        legend: { display: false },
                        tooltip: {
                            backgroundColor: '#44403C',
                            titleColor: '#FFFFFF',
                            bodyColor: '#FFFFFF',
                            cornerRadius: 8,
                        }
                    }
                }
            });
        });
    </script>
</body>
