                    tooltip: {
                        backgroundColor: '#44403C',
                        titleColor: '#FFFBF5',
                        bodyColor: '#FFEDD5',
                        padding: 10,
                        cornerRadius: 8,
                        boxPadding: 5
                    }
                }
            }
        });
    </script>
