            margin: 10px 0;
        }

        .stat-label {
            color: var(--text);
            font-size: 1rem;
        }

        .mermaid {
            background: white;
            padding: 20px;
            border-radius: 10px;
            margin: 20px 0;
        }

        .user-card {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
            padding: 15px;
            background: white;
            border-radius: 10px;
