        .stat-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 8px 25px rgba(255, 107, 53, 0.15);
        }

        .stat-value {
            font-size: 2.8rem;
            font-weight: 700;
            color: var(--primary);
