    <style>
        :root {
            --primary: #FF9A76;
            --secondary: #FFD8CC;
            --accent: #FF7043;
            --light: #FFF4F0;
            --dark: #5D4037;
            --text: #4E342E;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
        }

        body {
            background: linear-gradient(135deg, var(--light) 0%, #FFECDD 100%);
            color: var(--text);
            line-height: 1.8;
            padding: 20px;
            min-height: 100vh;
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
        }

        header {
            background: rgba(255, 255, 255, 0.9);
            border-radius: 16px;
            padding: 30px;
            margin-bottom: 30px;
            box-shadow: 0 8px 20px rgba(255, 106, 67, 0.15);
            text-align: center;
            border: 1px solid var(--secondary);
        }

        h1 {
            color: var(--accent);
            font-size: 2.5rem;
            margin-bottom: 10px;
        }

        .stats-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: 20px;
            margin-top: 25px;
        }

        .stat-card {
            background: white;
            border-radius: 12px;
            padding: 20px;
            text-align: center;
            box-shadow: 0 4px 12px rgba(255, 106, 67, 0.1);
            transition: transform 0.3s ease;
        }

        .stat-card:hover {
            transform: translateY(-5px);
        }

        .stat-value {
            font-size: 2.2rem;
            font-weight: 700;
            color: var(--accent);
            margin: 10px 0;
        }

        .stat-label {
            color: var(--dark);
            font-size: 1.1rem;
        }

        .section-title {
            font-size: 1.8rem;
            color: var(--accent);
            margin: 40px 0 20px;
            padding-bottom: 10px;
            border-bottom: 3px solid var(--secondary);
        }

        .charts-container {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
            gap: 30px;
            margin-bottom: 40px;
        }

        .chart-card {
            background: white;
            border-radius: 16px;
            padding: 25px;
            box-shadow: 0 8px 20px rgba(255, 106, 67, 0.1);
        }

        .user-list {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
            gap: 20px;
            margin: 30px 0;
        }

        .user-card {
            background: white;
            border-radius: 12px;
            padding: 20px;
            box-shadow: 0 4px 12px rgba(255, 106, 67, 0.1);
            display: flex;
            align-items: center;
            transition: all 0.3s ease;
        }

        .user-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 8px 16px rgba(255, 106, 67, 0.2);
        }

        .user-avatar {
            width: 50px;
            height: 50px;
            background: var(--secondary);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            margin-right: 15px;
            font-size: 1.5rem;
            color: var(--accent);
        }

        .user-name {
            font-weight: 600;
            font-size: 1.2rem;
        }

        .user-messages {
            color: var(--accent);
            font-weight: 700;
        }

        .topics-container {
            background: white;
            border-radius: 16px;
            padding: 30px;
            margin: 30px 0;
            box-shadow: 0 8px 20px rgba(255, 106, 67, 0.1);
        }

        .topic {
            margin-bottom: 25px;
            padding-bottom: 25px;
            border-bottom: 1px dashed var(--secondary);
        }

        .topic:last-child {
            border-bottom: none;
            margin-bottom: 0;
            padding-bottom: 0;
        }

        .topic-title {
            font-size: 1.4rem;
            color: var(--accent);
            margin-bottom: 15px;
            display: flex;
            align-items: center;
        }

        .topic-title i {
            margin-right: 10px;
            background: var(--secondary);
            width: 36px;
            height: 36px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            color: var(--accent);
        }

        .topic-content {
            background: var(--light);
            padding: 20px;
            border-radius: 12px;
            line-height: 1.7;
        }

        .mermaid {
            background: white;
            padding: 20px;
            border-radius: 12px;
            margin: 20px 0;
            overflow: auto;
        }

        @media (max-width: 768px) {
            .charts-container {
                grid-template-columns: 1fr;
            }

            .stats-grid {
                grid-template-columns: 1fr;
            }

            h1 {
                font-size: 2rem;
            }
        }
    </style>
</head>
