    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
    <style>
        :root {
            --bg-color: #FFFAF0; /* 米白 */
            --card-bg-color: rgba(255, 250, 240, 0.7);
            --text-color: #4A4A4A;
            --heading-color: #8C5B2F; /* 赭石 */
            --accent-color: #D4A266; /* 淡橙 */
            --highlight-color: #FDBA74; /* 亮橙 */
            --border-color: rgba(140, 91, 47, 0.2);
            --shadow-color: rgba(140, 91, 47, 0.1);
            --font-main: 'Noto Sans SC', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
        }

        * {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        html {
            scroll-behavior: smooth;
        }

        body {
            font-family: var(--font-main);
            background-color: var(--bg-color);
            color: var(--text-color);
            line-height: 1.8;
            padding: 2rem;
            background-image:
                radial-gradient(circle at 10% 20%, rgba(253, 186, 116, 0.15) 0%, transparent 40%),
                radial-gradient(circle at 80% 90%, rgba(212, 162, 102, 0.15) 0%, transparent 40%);
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
        }

        header {
            text-align: center;
            margin-bottom: 3rem;
        }

        h1 {
            font-size: 2.5rem;
            color: var(--heading-color);
            font-weight: 700;
            margin-bottom: 0.5rem;
        }

        .subtitle {
            font-size: 1.1rem;
            color: var(--accent-color);
        }

        .bento-grid {
            display: grid;
            gap: 1.5rem;
            grid-template-columns: repeat(4, 1fr);
            grid-auto-rows: minmax(100px, auto);
        }

        .card {
            background-color: var(--card-bg-color);
            border: 1px solid var(--border-color);
            border-radius: 20px;
            padding: 1.5rem;
            box-shadow: 0 8px 24px var(--shadow-color);
            backdrop-filter: blur(10px);
            -webkit-backdrop-filter: blur(10px);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .card:hover {
            transform: translateY(-5px);
            box-shadow: 0 12px 32px var(--shadow-color);
        }

        .card-2x2 { grid-column: span 2; grid-row: span 2; }
        .card-2x1 { grid-column: span 2; }
        .card-1x1 { grid-column: span 1; }
        .card-4x1 { grid-column: span 4; }

        .card h2 {
            font-size: 1.5rem;
            color: var(--heading-color);
            margin-bottom: 1rem;
            display: flex;
            align-items: center;
        }

        .card h2 i {
            margin-right: 0.75rem;
            color: var(--accent-color);
        }

        .card h3 {
            font-size: 1.1rem;
            color: var(--heading-color);
            margin-top: 1.5rem;
            margin-bottom: 0.5rem;
            font-weight: 600;
        }

        /* Specific Card Styles */
        .stats-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 1rem;
            height: 100%;
            align-content: center;
        }

        .stat-item {
            text-align: center;
            padding: 1rem;
        }

        .stat-item .value {
            font-size: 2rem;
            font-weight: 700;
            color: var(--heading-color);
        }

        .stat-item .label {
            font-size: 0.9rem;
            color: var(--accent-color);
        }

        .keyword-tags {
            display: flex;
            flex-wrap: wrap;
            gap: 0.75rem;
            padding-top: 0.5rem;
        }

        .keyword-tag {
            background-color: rgba(212, 162, 102, 0.2);
            color: var(--heading-color);
            padding: 0.5rem 1rem;
            border-radius: 999px;
            font-size: 0.9rem;
            font-weight: 500;
            transition: background-color 0.3s ease;
        }

        .keyword-tag:hover {
            background-color: rgba(212, 162, 102, 0.4);
        }

        .mermaid {
            width: 100%;
            min-height: 250px;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .topic-description {
            font-size: 1rem;
            padding-left: 1rem;
            border-left: 3px solid var(--highlight-color);
        }

        .dialogue-container {
            margin-top: 1rem;
            padding: 1rem;
            background-color: rgba(0,0,0,0.02);
            border-radius: 10px;
        }

        .message-bubble {
            max-width: 90%;
            padding: 0.75rem 1.25rem;
            border-radius: 15px;
            margin-bottom: 0.75rem;
            background-color: #fff;
            border: 1px solid var(--border-color);
        }

        .message-bubble .author {
            font-weight: 600;
            color: var(--accent-color);
            margin-bottom: 0.25rem;
            font-size: 0.9rem;
        }

        .message-bubble .content {
            word-wrap: break-word;
        }

        .quote-card {
            background: linear-gradient(135deg, #FFF8F0, #FEFDFB);
            border-left: 5px solid var(--highlight-color);
            height: 100%;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }

        .quote-text {
            font-size: 1.2rem;
            font-style: italic;
            color: var(--heading-color);
            margin-bottom: 1rem;
            position: relative;
            padding-left: 2rem;
        }

        .quote-text::before {
            content: '\f10d';
            font-family: 'Font Awesome 6 Free';
            font-weight: 900;
            position: absolute;
            left: 0;
            top: 0;
            color: var(--highlight-color);
        }

        .quote-author {
            text-align: right;
            font-weight: 600;
            color: var(--accent-color);
            margin-bottom: 1rem;
        }

        .interpretation-area {
            font-size: 0.9rem;
            background-color: rgba(0,0,0,0.02);
            padding: 0.75rem;
            border-radius: 8px;
            border-top: 1px solid var(--border-color);
        }

        .interpretation-area strong {
            color: var(--heading-color);
        }

        .resource-list li {
            list-style: none;
            margin-bottom: 0.75rem;
            display: flex;
            align-items: flex-start;
        }

        .resource-list li i {
            margin-right: 0.75rem;
            margin-top: 5px;
            color: var(--accent-color);
        }

        .resource-list a {
            color: var(--heading-color);
            text-decoration: none;
            font-weight: 600;
            border-bottom: 2px solid transparent;
            transition: border-color 0.3s ease;
        }

        .resource-list a:hover {
            border-color: var(--highlight-color);
        }

        footer {
            text-align: center;
            margin-top: 3rem;
            font-size: 0.9rem;
            color: var(--accent-color);
        }

        /* Responsive Design */
        @media (max-width: 992px) {
            .bento-grid {
                grid-template-columns: repeat(2, 1fr);
            }
            .card-4x1 { grid-column: span 2; }
        }

        @media (max-width: 768px) {
            body { padding: 1rem; }
            h1 { font-size: 2rem; }
            .bento-grid {
                grid-template-columns: 1fr;
            }
            .card-2x2, .card-2x1, .card-1x1, .card-4x1 {
                grid-column: span 1;
            }
            .stats-grid {
                grid-template-columns: 1fr;
                gap: 0;
            }
        }
    </style>
</head>
