    <style>
        :root {
            --primary-color: #FF9A3C;
            --secondary-color: #FFC93C;
            --accent-color: #FF6B6B;
            --light-color: #FFF5E4;
            --dark-color: #5C3D2E;
            --text-color: #4A403A;
        }

        body {
            font-family: 'PingFang SC', 'Microsoft YaHei', sans-serif;
            background-color: var(--light-color);
            color: var(--text-color);
            line-height: 1.6;
            margin: 0;
            padding: 0;
        }

        .container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 20px;
        }

        header {
            background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
            color: white;
            padding: 30px 20px;
            border-radius: 15px;
            margin-bottom: 30px;
            box-shadow: 0 10px 20px rgba(255, 154, 60, 0.2);
            text-align: center;
        }

        h1 {
            margin: 0;
            font-size: 2.2rem;
            font-weight: 700;
        }

        h2 {
            color: var(--primary-color);
            border-bottom: 2px solid var(--secondary-color);
            padding-bottom: 10px;
            margin-top: 40px;
        }

        .card {
            background-color: white;
            border-radius: 12px;
            padding: 25px;
            margin-bottom: 30px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
        }

        .keyword-tag {
            display: inline-block;
            background-color: var(--secondary-color);
            color: var(--dark-color);
            padding: 8px 15px;
            border-radius: 20px;
            margin: 5px;
            font-weight: 600;
            box-shadow: 0 3px 6px rgba(0, 0, 0, 0.1);
        }

        .message-bubble {
            max-width: 80%;
            padding: 12px 18px;
            border-radius: 18px;
            margin-bottom: 15px;
            position: relative;
        }

        .message-left {
            background-color: #FFF0E0;
            border-top-left-radius: 5px;
            margin-right: auto;
        }

        .message-right {
            background-color: #FFE4C4;
            border-top-right-radius: 5px;
            margin-left: auto;
        }

        .speaker-info {
            font-size: 0.8rem;
            color: var(--text-color);
            opacity: 0.7;
            margin-bottom: 5px;
        }

        .quote-card {
            background-color: #FFF8F0;
            border-left: 4px solid var(--primary-color);
            padding: 20px;
            margin-bottom: 20px;
            border-radius: 8px;
        }

        .quote-text {
            font-size: 1.1rem;
            font-style: italic;
            color: var(--dark-color);
        }

        .quote-author {
            text-align: right;
            font-size: 0.9rem;
            color: var(--text-color);
            opacity: 0.8;
        }

        .stats-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: 20px;
            margin-bottom: 30px;
        }

        .stat-card {
            background-color: white;
            border-radius: 12px;
            padding: 20px;
            text-align: center;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
        }

        .stat-number {
            font-size: 2.5rem;
            font-weight: 700;
            color: var(--primary-color);
            margin: 10px 0;
        }

        .stat-label {
            font-size: 1rem;
            color: var(--text-color);
            opacity: 0.8;
        }

        .empty-state {
            text-align: center;
            padding: 50px 20px;
            color: var(--text-color);
            opacity: 0.6;
        }

        .empty-state i {
            font-size: 3rem;
            margin-bottom: 20px;
            color: var(--secondary-color);
        }

        @media (max-width: 768px) {
            h1 {
                font-size: 1.8rem;
            }

            .stats-grid {
                grid-template-columns: 1fr;
            }
        }
    </style>
</head>
<body>
    <div class="container">
        <header>
