            padding-top: 2rem;
            font-size: 0.9rem;
            color: var(--secondary-text-color);
        }

        @media (max-width: 1200px) {
            .chart-card { grid-column: 1 / -1; }
            .activity-card { grid-column: 1 / -1; }
            .keywords-card { grid-column: 1 / 6; }
            .quotes-card { grid-column: 6 / -1; }
        }

        @media (max-width: 992px) {
            .keywords-card { grid-column: 1 / -1; }
            .quotes-card { grid-column: 1 / -1; }
        }

        @media (max-width: 768px) {
            body { padding: 1rem; }
