            primaryBg: 'rgba(229, 124, 35, 0.2)',
            secondary: 'rgba(210, 180, 140, 1)',
            text: '#5C4033',
            grid: 'rgba(92, 64, 51, 0.1)'
        };

        // 1. Top Active Users Chart (Bar)
        const ctxUsers = document.getElementById('activeUsersChart').getContext('2d');
        const activeUsersChart = new Chart(ctxUsers, {
            type: 'bar',
            data: {
                labels: topUsersData.labels,
                datasets: [{
                    label: '消息数量',
                    data: topUsersData.messages,
                    backgroundColor: chartColors.primaryLight,
                    borderColor: chartColors.primary,
                    borderWidth: 2,
                    borderRadius: 5,
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                indexAxis: 'y',
                scales: {
                    x: {
                        beginAtZero: true,
                        grid: {
                            color: chartColors.grid
                        },
                        ticks: {
                            color: chartColors.text
                        }
                    },
                    y: {
                        grid: {
                            display: false
                        },
                        ticks: {
                            color: chartColors.text
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    },
                    tooltip: {
                        backgroundColor: '#fff',
                        titleColor: chartColors.text,
                        bodyColor: chartColors.text,
                        borderColor: chartColors.secondary,
                        borderWidth: 1
                    }
                }
            }
        });

        // 2. Hourly Activity Chart (Line)
        const ctxActivity = document.getElementById('activityHourChart').getContext('2d');
        const activityHourChart = new Chart(ctxActivity, {
            type: 'line',
            data: {
                labels: hourlyActivityData.labels,
                datasets: [{
                    label: '消息数量',
                    data: hourlyActivityData.messages,
                    fill: true,
                    backgroundColor: chartColors.primaryBg,
                    borderColor: chartColors.primary,
                    tension: 0.4,
                    pointBackgroundColor: chartColors.primary,
                    pointRadius: 4,
                    pointHoverRadius: 7,
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                 scales: {
                    y: {
                        beginAtZero: true,
                         grid: {
                            color: chartColors.grid
                        },
                        ticks: {
                            color: chartColors.text
                        }
                    },
                    x: {
                         grid: {
                            color: chartColors.grid
                        },
                        ticks: {
                            color: chartColors.text
                        }
                    }
                },
                plugins: {
                    legend: {
                        display: false
                    },
                    tooltip: {
                         backgroundColor: '#fff',
                        titleColor: chartColors.text,
                        bodyColor: chartColors.text,
                        borderColor: chartColors.secondary,
                        borderWidth: 1
                    }
                }
            }
        });
    </script>

